module.exports = api => {
  return {
    getDatabaseInfo: async (server_id) => {
      return await api.send("getDatabaseInfo", server_id, {server_id});
    },
    
    createDatabase: async (server_id) => {
      return await api.send("createDatabase", server_id, {server_id});
    },
    
    changeDatabasePassword: async (server_id) => {
      return await api.send("changeDatabasePassword", server_id, {server_id});
    },
    
    deleteDatabase: async (server_id) => {
      return await api.send("deleteDatabase", server_id, {server_id});
    },
  };
};
