module.exports = api => {
  return {
    listSettings: async () => {
      return await api.send("listSettings");
    },
    
    getSetting: async (key) => {
      return await api.send("deleteSetting", key, {key});
    },
    
    setSetting: async (key, value) => {
      return await api.send("deleteSetting", `${key}${value}`, {key, value});
    },
    
    deleteSetting: async (key) => {
      return await api.send("deleteSetting", key, {key});
    },
  };
};
